import React, { useState } from 'react';
import MiningStats from './MiningStats';
import NodeManager from './nodemanager';
import MinerManager from './MinerManager';
import GpuMonitor from './GpuMonitor';
import './App.css';

// If your NodeManager and MinerManager expect these props, make sure their types accept them
function App() {
  const [page, setPage] = useState<'mining' | 'node' | 'gpu'>('mining');
  const [network, setNetwork] = useState<"mainnet" | "testnet">("mainnet");

  return (
    <div className="App">
      <header>
        <h1>CortexMining Dashboard</h1>
        <nav style={{ margin: "1em 0" }}>
          <button onClick={() => setPage('mining')} style={{ marginRight: 12 }}>
            Mining Dashboard
          </button>
          <button onClick={() => setPage('node')} style={{ marginRight: 12 }}>
            Node Manager
          </button>
          <button onClick={() => setPage('gpu')}>
            GPU Monitor
          </button>
          {/* Network Switcher */}
          <span style={{ marginLeft: 24 }}>
            <button
              onClick={() => setNetwork("mainnet")}
              style={{
                background: network === "mainnet" ? "#0078ff" : "#222",
                color: "#fff",
                marginRight: 6,
              }}
            >
              Mainnet
            </button>
            <button
              onClick={() => setNetwork("testnet")}
              style={{
                background: network === "testnet" ? "#e38a00" : "#222",
                color: "#fff"
              }}
            >
              Testnet
            </button>
          </span>
        </nav>
        <hr />
      </header>
      <main>
        {page === 'mining' && (
          <MinerManager network={network} />
        )}
        {page === 'node' && (
          <NodeManager network={network} setNetwork={setNetwork} />
        )}
        {page === 'gpu' && <GpuMonitor />}
      </main>
    </div>
  );
}

export default App;
