import React, { useState } from 'react';
import MiningStats from './MiningStats';
import NodeManager from './nodemanager';
import MinerManager from './MinerManager';
import './App.css';

function App() {
  const [page, setPage] = useState('mining');
  const [network, setNetwork] = useState<"mainnet" | "testnet">("mainnet");

  return (
    <div className="App">
      <header>
        <h1>Ergo Mining Dashboard</h1>
        <nav style={{ margin: "1em 0" }}>
          <button onClick={() => setPage('mining')} style={{ marginRight: 12 }}>
            Mining Dashboard
          </button>
          <button onClick={() => setPage('node')}>
            Node Manager
          </button>
          {/* Network Switcher */}
          <span style={{ marginLeft: 24 }}>
            <button
              onClick={() => setNetwork("mainnet")}
              style={{
                background: network === "mainnet" ? "#0078ff" : "#222",
                color: "#fff",
                marginRight: 6,
              }}
            >
              Mainnet
            </button>
            <button
              onClick={() => setNetwork("testnet")}
              style={{
                background: network === "testnet" ? "#e38a00" : "#222",
                color: "#fff"
              }}
            >
              Testnet
            </button>
          </span>
        </nav>
        <hr />
      </header>
      <main>
        {page === 'mining' && (
          <MinerManager network={network} />
        )}
        {page === 'node' && (
          <NodeManager network={network} setNetwork={setNetwork} />
        )}
      </main>
    </div>
  );
}

export default App;
