import React, { useEffect, useState, useRef } from "react";
import Grid from "@mui/material/Grid";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import Slider from "@mui/material/Slider";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import Snackbar from "@mui/material/Snackbar";
import Alert from "@mui/material/Alert";
import { LineChart, Line, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from "recharts";
import SettingsIcon from "@mui/icons-material/Settings";

// ---- Animated Circuit BG ----
function AnimatedCircuitBG() {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    let stop = false;
    const ctx = canvasRef.current?.getContext("2d");
    if (!ctx) return;

    const W = window.innerWidth;
    const H = window.innerHeight;
    if (canvasRef.current) {
      canvasRef.current.width = W;
      canvasRef.current.height = H;
    }

    const nodes: { x: number; y: number; dir: number }[] = [];
    for (let i = 0; i < 48; i++) {
      nodes.push({
        x: Math.random() * W,
        y: Math.random() * H,
        dir: Math.random() * Math.PI * 2,
      });
    }
    function draw() {
      if (stop || !ctx) return;
      ctx.clearRect(0, 0, W, H);
      ctx.globalAlpha = 0.11;
      ctx.fillStyle = "#0b0f14";
      ctx.fillRect(0, 0, W, H);
      ctx.globalAlpha = 1;
      for (let i = 0; i < nodes.length; i++) {
        nodes[i].x += Math.cos(nodes[i].dir) * 0.8;
        nodes[i].y += Math.sin(nodes[i].dir) * 0.8;
        if (nodes[i].x < 0 || nodes[i].x > W) nodes[i].dir = Math.PI - nodes[i].dir;
        if (nodes[i].y < 0 || nodes[i].y > H) nodes[i].dir = -nodes[i].dir;
        ctx.beginPath();
        ctx.arc(nodes[i].x, nodes[i].y, 3, 0, Math.PI * 2);
        ctx.shadowColor = "#4bf7e9";
        ctx.shadowBlur = 14;
        ctx.fillStyle = "#28ffe7";
        ctx.fill();
        ctx.shadowBlur = 0;
        for (let j = i + 1; j < nodes.length; j++) {
          const dx = nodes[i].x - nodes[j].x, dy = nodes[i].y - nodes[j].y;
          const dist = Math.sqrt(dx * dx + dy * dy);
          if (dist < 140) {
            ctx.strokeStyle = "rgba(75,247,233,0.18)";
            ctx.lineWidth = 1.4;
            ctx.beginPath();
            ctx.moveTo(nodes[i].x, nodes[i].y);
            ctx.lineTo(nodes[j].x, nodes[j].y);
            ctx.stroke();
          }
        }
      }
      requestAnimationFrame(draw);
    }
    draw();
    return () => { stop = true; };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        zIndex: 0,
        pointerEvents: "none",
        filter: "blur(0.5px) brightness(0.98)"
      }}
    />
  );
}
// ---- End Animated Background ----

type GpuStat = {
  index: number;
  name: string;
  temp: number;
  util: number;
  fan: number;
  coreClock: number;
  memClock: number;
  power: number;
  powerLimit: number;
  memUsed: number;
  memTotal: number;
  controls?: {
    fan?: boolean;
    power?: boolean;
    coreClock?: boolean;
    memClock?: boolean;
  };
};
type HistoryPoint = GpuStat & { time: number };

export default function GpuMonitor() {
  const [gpus, setGpus] = useState<GpuStat[]>([]);
  const [history, setHistory] = useState<{ [idx: number]: HistoryPoint[] }>({});
  const [ocValues, setOcValues] = useState<{ [idx: number]: any }>({});
  const [snackbar, setSnackbar] = useState<{ msg: string, type: "success" | "error" } | null>(null);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await fetch("/api/gpu/stats");
        const data = await res.json();
        if (!data.gpus) throw new Error("No GPU stats found");
        setGpus(
          data.gpus.map((gpu: GpuStat) => ({
            ...gpu,
            controls: gpu.controls || { fan: true, power: true, coreClock: true, memClock: true },
          }))
        );
        setHistory(prev => {
          const updated = { ...prev };
          data.gpus.forEach((gpu: GpuStat) => {
            if (!updated[gpu.index]) updated[gpu.index] = [];
            const arr = [...updated[gpu.index], { ...gpu, time: Date.now() }];
            updated[gpu.index] = arr.slice(-30);
          });
          return updated;
        });
      } catch (err: any) {
        setSnackbar({ msg: err.message, type: "error" });
      }
    };
    fetchStats();
    intervalRef.current = setInterval(fetchStats, 2000);
    return () => { if (intervalRef.current) clearInterval(intervalRef.current); };
  }, []);

  const handleOcChange = (idx: number, field: string, value: number) => {
    setOcValues(prev => ({
      ...prev,
      [idx]: { ...prev[idx], [field]: value }
    }));
  };

  const handleOcApply = async (gpu: GpuStat) => {
    const values = ocValues[gpu.index] || {};
    try {
      const body: any = { index: gpu.index };
      if (gpu.controls?.fan && values.fan !== undefined) body.fan = values.fan;
      if (gpu.controls?.power && values.powerLimit !== undefined) body.powerLimit = values.powerLimit;
      if (gpu.controls?.coreClock && values.coreClock !== undefined) body.coreClock = values.coreClock;
      if (gpu.controls?.memClock && values.memClock !== undefined) body.memClock = values.memClock;
      if (Object.keys(body).length <= 1) {
        setSnackbar({ msg: "No changes selected.", type: "error" });
        return;
      }
      const res = await fetch("/api/gpu/overclock", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body)
      });
      const resp = await res.json();
      if (res.ok) {
        setSnackbar({ msg: "Settings applied!", type: "success" });
      } else {
        setSnackbar({ msg: resp.error || "Failed to apply settings", type: "error" });
      }
    } catch (err: any) {
      setSnackbar({ msg: err.message, type: "error" });
    }
  };

  // --- Enhanced Full Width Layout ---
  const cardGradient = "linear-gradient(135deg, #13151e 70%, #0f232c 100%)";
  const cardBorder = "0 0 18px 3px #20ffe2b0, 0 0 40px 6px #58dcff40";

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        width: "100vw",
        overflow: "hidden",
        fontFamily: "'IBM Plex Mono', 'Fira Mono', 'Consolas', 'monospace', 'Inter', sans-serif",
        color: "#e5f9ff"
      }}
    >
      <AnimatedCircuitBG />
      <Box
        sx={{
          position: "relative",
          zIndex: 2,
          pt: 8,
          px: { xs: 1, md: 8, xl: 18 },
          width: "100%",
          minHeight: "100vh"
        }}
      >
        <Typography
          variant="h3"
          sx={{
            color: "#19fff6",
            fontWeight: 800,
            letterSpacing: "0.04em",
            mb: 4,
            textAlign: "center",
            textShadow: "0 0 24px #2efbffa0, 0 0 8px #fff"
          }}
        >
          <SettingsIcon fontSize="large" sx={{ verticalAlign: "middle", mr: 1 }} />
          GPU Monitor &amp; Control
        </Typography>
        <Grid container spacing={6} justifyContent="center">
          {gpus.map(gpu => {
            const controls = gpu.controls || { fan: true, power: true, coreClock: true, memClock: true };
            return (
              <Grid item xs={12} md={10} lg={8} key={gpu.index}>
                <Card
                  sx={{
                    borderRadius: 5,
                    background: cardGradient,
                    boxShadow: cardBorder,
                    border: "2.2px solid #4bf7e9bb",
                    mb: 5,
                    minWidth: 420,
                    width: "100%"
                  }}
                >
                  <CardContent>
                    <Typography variant="h5" sx={{ color: "#4bf7e9", fontWeight: 700, mb: 2 }}>
                      {gpu.name} (#{gpu.index})
                    </Typography>
                    <Box sx={{ display: "flex", gap: 4, flexWrap: "wrap", alignItems: "center", mb: 2 }}>
                      <Typography variant="h4" sx={{ color: "#ffbe0b", textShadow: "0 0 14px #fffbe055" }}>
                        {gpu.temp}°C
                      </Typography>
                      <Typography variant="h5" sx={{ color: "#20ffe2" }}>
                        {gpu.power}W
                      </Typography>
                      <Typography variant="h5" sx={{ color: "#2979ff" }}>
                        {gpu.util}%
                      </Typography>
                      <Typography sx={{ color: "#b2b2b2" }}>
                        Fan: {gpu.fan}% | Clock: {gpu.coreClock} MHz | Mem: {gpu.memClock} MHz
                      </Typography>
                      <Typography sx={{ color: "#b2b2b2" }}>
                        VRAM: {gpu.memUsed} / {gpu.memTotal} MiB
                      </Typography>
                    </Box>
                    <Box sx={{ height: 200, mt: 2, mb: 2 }}>
                      <ResponsiveContainer width="100%" height="100%">
                        <LineChart data={history[gpu.index] || []}>
                          <XAxis dataKey="time" hide />
                          <YAxis domain={['auto', 'auto']} />
                          <Tooltip
                            labelFormatter={t => new Date(t).toLocaleTimeString()}
                            formatter={(v: any, n: any) => [v, n.toUpperCase()]}
                          />
                          <Legend />
                          <Line type="monotone" dataKey="temp" stroke="#ffbe0b" dot={false} name="Temp" />
                          <Line type="monotone" dataKey="power" stroke="#20ffe2" dot={false} name="Power" />
                          <Line type="monotone" dataKey="util" stroke="#2979ff" dot={false} name="Util" />
                        </LineChart>
                      </ResponsiveContainer>
                    </Box>
                    <Box sx={{ mt: 3 }}>
                      {controls.fan && (
                        <Box mb={2}>
                          <Typography>Fan Speed (%)</Typography>
                          <Slider
                            min={0} max={100}
                            value={ocValues[gpu.index]?.fan ?? gpu.fan}
                            onChange={(_, v) => handleOcChange(gpu.index, "fan", v as number)}
                            valueLabelDisplay="auto"
                            sx={{ color: "#ffbe0b" }}
                          />
                        </Box>
                      )}
                      {controls.power && (
                        <Box mb={2}>
                          <Typography>Power Limit (W)</Typography>
                          <Slider
                            min={50} max={gpu.powerLimit}
                            value={ocValues[gpu.index]?.powerLimit ?? gpu.powerLimit}
                            onChange={(_, v) => handleOcChange(gpu.index, "powerLimit", v as number)}
                            valueLabelDisplay="auto"
                            sx={{ color: "#20ffe2" }}
                          />
                        </Box>
                      )}
                      {controls.coreClock && (
                        <Box mb={2}>
                          <Typography>Core Clock Offset (MHz)</Typography>
                          <Slider
                            min={-300} max={300}
                            value={ocValues[gpu.index]?.coreClock ?? 0}
                            onChange={(_, v) => handleOcChange(gpu.index, "coreClock", v as number)}
                            valueLabelDisplay="auto"
                            sx={{ color: "#2979ff" }}
                          />
                        </Box>
                      )}
                      {controls.memClock && (
                        <Box mb={2}>
                          <Typography>Memory Clock Offset (MHz)</Typography>
                          <Slider
                            min={-1000} max={2000}
                            value={ocValues[gpu.index]?.memClock ?? 0}
                            onChange={(_, v) => handleOcChange(gpu.index, "memClock", v as number)}
                            valueLabelDisplay="auto"
                            sx={{ color: "#ab47bc" }}
                          />
                        </Box>
                      )}
                      {(controls.fan || controls.power || controls.coreClock || controls.memClock) && (
                        <Button
                          variant="contained"
                          color="primary"
                          sx={{
                            mt: 2,
                            background: "linear-gradient(90deg, #19fff6 30%, #4bf7e9 90%)",
                            color: "#191b1f",
                            fontWeight: 700,
                            borderRadius: 2,
                            boxShadow: "0 0 14px 0 #19fff680",
                            '&:hover': { background: "#12e9df" }
                          }}
                          onClick={() => handleOcApply(gpu)}
                          startIcon={<SettingsIcon />}
                        >
                          Apply Changes
                        </Button>
                      )}
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            );
          })}
        </Grid>
      </Box>
      <Snackbar
        open={!!snackbar}
        autoHideDuration={4800}
        onClose={() => setSnackbar(null)}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
      >
        <Alert onClose={() => setSnackbar(null)} severity={snackbar?.type || "success"}>
          {snackbar?.msg}
        </Alert>
      </Snackbar>
    </Box>
  );
}
